!function(){function e(){var e=document.querySelector(".main"),n=e.offsetTop;window.scrollY>=n?document.body.classList.add("sticky-nav"):document.body.classList.remove("sticky-nav")}window.addEventListener("scroll",e);var n=document.querySelector(".read-more");n.addEventListener("click",function(e){e.preventDefault();var o=document.querySelector(".more");o.classList.contains("showing")?(o.classList.remove("showing"),n.innerHTML="Read<br>More"):(o.classList.add("showing"),n.innerHTML="Read<br>Less")})}();